import React from 'react';
import {CarouselData} from './carousel_data';
import Slider from 'react-slick';
// import '~slick-carousel/slick/slick.css';
// import '~slick-carousel/slick/slick-theme.css';
import img1 from './carousel_img/chain.jpg';
import img2 from './carousel_img/753.jpg';
import img3 from './carousel_img/about.jpg';
import img4 from './carousel_img/two.jpg';

const photos =[
  {
    name:img1,
    url:'./carousel_img/chain.jpg'
  },
  {
    name:img3,
    url:'./carousel_img/about.jpg'
  },
]


class ContentCarousel extends React.Component {
    render() {
      const settings= {
        dots:true,
        fade:true,
        infinte:true,
        speed:500,
        slidesToShow:1,
        arrows:true,
        slidesToScroll:1,
        className:"slides"
      }
      return (
        <div className="ContentCarousel">
          <Slider {...settings}>
          {photos.map((photo)=>{
            return(
              <div>
                <img src={photo.name}/>
              </div>
            )
          })}
          </Slider>
          </div>
      )
    }
  }
export default ContentCarousel
