import React from 'react';


class SearchBox extends React.Component{
  constructor(props) {
       super(props);
       this.state = {
           style : {
               width : 350
           }
       };
       this.openNav = this.openNav.bind(this);
       this.closeNav = this.closeNav.bind(this);
   }

   componentDidMount() {
       document.addEventListener("click", this.closeNav);
   }

   componentWillUnmount() {
       document.removeEventListener("click", this.closeNav);
   }

   openNav() {
       const style = { width : 350 };
       this.setState({ style });
       document.body.style.backgroundColor = "rgba(0,0,0,0.4)";
       document.addEventListener("click", this.closeNav);
   }

   closeNav() {
       document.removeEventListener("click", this.closeNav);
       const style = { width : 0 };
       this.setState({ style });
       document.body.style.backgroundColor = "#F3F3F3";
   }


  render(){
      const style = this.state.block ? "display: none" : "display:block"
    return(
      <div>
        <div id="myOverlay" class="overlay">
          <span class="closebtn" onClick   = {this.closeNav} title="Close Overlay">Ã—</span>
          <div class="overlay-content">
            <form action="/action_page.php">
              <input type="text" placeholder="Search.." name="search"/>
              <button type="submit"><i class="fa fa-search"></i></button>
            </form>
          </div>
        </div>

        <h2>Fullscreen Search</h2>
        <p>Click on the button to open the search box.</p>
        <button class="openBtn" onClick={this.openNav}>Open Search Box</button>
      </div>
    )
  }
}

export default SearchBox
