import React from 'react';
import './search.css'
import { makeStyles } from '@material-ui/core/styles';


const useStyles = makeStyles(theme => ({
      openbtn: {
        display: 'block',
      },
    }));

class SearchBox extends React.Component{

  render(){
      const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const closeSearch = () => {
     setOpen(false);
   };
   const openSearch = () => {
     setOpen(!open);
   };
    return(
      <div>
        <div id="myOverlay" class="overlay">
          <span class="closebtn" onclick={closeSearch} title="Close Overlay">hiii</span>
          <div class="overlay-content">
            <form action="/action_page.php">
              <input type="text" placeholder="Search.." name="search"/>
              <button type="submit"><i class="fa fa-search"></i></button>
            </form>
          </div>
        </div>

        <h2>Fullscreen Search</h2>
        <p>Click on the button to open the search box.</p>
        <button className={classes.openbtn} onclick={openSearch}>Open Search Box</button>
      </div>
    )
  }
}

export default SearchBox
