import React from 'react';
import 'react-router-dom';
import {ListItem} from './list_items';
import './navbar.css';




class Navbar extends React.Component{
  constructor(props){
    super(props);
    this.state ={clicked:false}
  }

  handleClick = ()=>{
    this.setState({ clicked: !this.state.clicked})
  }
  render(){
    const [navBackground,useState]=(false);
      const navRef = useRef();
      navRef.current = navBackground;
      useEffect(() => {
        const handleScroll = () => {
          const show = window.scrollY > 50
          if (navRef.current !== show) {
            setNavBackground(show)
          }
        }
        document.addEventListener('scroll', handleScroll)
        return () => {
          document.removeEventListener('scroll', handleScroll)
        }
      }, [])
    return(
      <nav className="NavbarItems" id="mobile" >
        <a className="navbar-brand col-2" href="#">
          <img className="logoimg" src='/img/logo/logoo1.png' alt="logo"/>
        </a>
        <div className="menu-icon" onClick={this.handleClick}>
          <i className={this.state.clicked? 'fas fa-times':
          'fas fa-bars'}></i>
        </div>
        <ul className={this.state.clicked ?'nav-menu active':
        'nav-menu'}>
          {ListItem.map((item,index) => {
            return(
              <li key={index}>
                  <a className={item.cName} href={item.url}>
                  {item.name}
                  </a>
              </li>
            )
          })}
        </ul>
      </nav>
    )
  }
}

export default Navbar
