import React from 'react';


class SearchBox extends React.Component{
  constructor(props){
    super(props);

  this.state = {
            block: True
  }
}
  changeOverlay() {
    this.setState({block: !this.state.block})
  }


  render(){
      const style = this.state.block ? "display:none" : "display:block"
    return(
      <div>
        <div id="myOverlay" class="overlay">
          <span class="closebtn" onclick={this.closeSearch()} title="Close Overlay">Ã—</span>
          <div class="overlay-content">
            <form action="/action_page.php">
              <input type="text" placeholder="Search.." name="search"/>
              <button type="submit"><i class="fa fa-search"></i></button>
            </form>
          </div>
        </div>

        <h2>Fullscreen Search</h2>
        <p>Click on the button to open the search box.</p>
        <button class="openBtn" onclick={this.openSearch()}>Open Search Box</button>
      </div>
    )
  }
}

export default SearchBox
