import React from 'react';
import './transaction_content.css';
import Json from './json.js';
import Modal from 'react-modal';

class TransactionContent extends React.Component{
  constructor(props){
    super(props)
      this.state = {
        data: [],
        cardIndex: null,
        isModalOpen: false
       };

      this.onValueChange = this.onValueChange.bind(this);
      this.formSubmit = this.formSubmit.bind(this);
       this.toggleModal = this.toggleModal.bind(this);
  }
  onSiteChanged(e) {
   this.setState({
     site: e.currentTarget.value
     });
 }
 toggleModal(id) {
   console.log(id);
   this.setState({
     cardIndex: id,
     isModalOpen: !this.state.isModalOpen
   });
 }
  onValueChange(event) {
    alert(`hii`);
    const requestOptions = {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(
          { type: this.state.data.type },
          { region: this.state.data.region },
          { weight: this.state.data.weight }
        )};
    fetch('/transaction/LocationPrice',requestOptions)
      .then(res => res.json())
      .then(json => this.setState({ data: json }));
    this.setState({
      selectedOption: event.target.value
    });
  }

  formSubmit(event) {
    event.preventDefault();
    alert(`You chose ${this.state.selectedOption}.`);
    console.log(this.state.selectedOption)
  }

    render(){
      if (!this.props.data){
        return ("oops")
      }
      const {cardIndex } = this.state;
      console.log(this.props.data.DeliveryInfo);
    return(
      <div id="transContent" className=" container-fluid mt-5">
        <section className="pricing py-5 p-5">
        <h1 className="text-center mb-5">Shipment cost</h1>
            <div className="row">
            {this.props.data.Info.map((item,index)=>{
              return(
              <div
              key={item.Option_ID}
              className="col-lg-4 col-12"
               onClick={e => this.toggleModal(index)}>
                <div className="card mb-5">
                  <div className="card-body">
                    <h4 className="card-title text-muted text-uppercase text-center">{item.title}</h4>
                    <h5 className="text-muted text-center">&#10038; {item.subtitle}</h5>
                    <h6 className="card-price text-center">{item.firstPrice} ILS
                      <span className="period">{item.span}</span>
                    </h6>
                    <ul className="fa-ul">
                        <li>
                          <span className="fa-li">
                          <i className="fas fa-check"></i>
                          </span>{item.text}
                        </li>
                    </ul>
                  </div>
                  <div className="card-footer">

                    <button className="btn btn-block text-uppercase" data-toggle="modal" data-target="#exampleModal">Continue</button>
                    <Modal
                       isOpen={this.state.isModalOpen}
                       toggle={e => this.toggleModal(cardIndex)}
                     >
                       <ModalHeader toggle={e => this.toggleModal(cardIndex)}>
                         {cardIndex !== null && item[cardIndex].title}
                       </ModalHeader>
                       <ModalBody>
                         {cardIndex !== null && item[cardIndex].subtitle}
                       </ModalBody>
                     </Modal>
                    <div class="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
                      <div class="modal-dialog" role="document">
                        <div class="modal-content">
                          <div class="modal-header">
                            <h5 class="modal-title" id="exampleModalLabel">Price</h5>
                            <button type="button" class="close" data-dismiss="modal" aria-label="Close">
                              <span aria-hidden="true">&times;</span>
                            </button>
                          </div>
                            <div class="modal-body">
                                <h6 className="card-price text-center"> ILS
                                  <span className="period">/Per package</span>
                                </h6>
                            </div>
                            <div class="form-group mt-3">
                              <form onSubmit={this.formSubmit}>
                               <div class="form-check">
                                 <label class="form-check-label" for="radio1">
                                 <input
                                     checked={this.state.selectedOption === "option1"}
                                     onChange={this.onValueChange}
                                     type="radio"
                                     class="form-check-input"
                                     id="radio1"
                                     name="optradio"
                                     value="option1"
                                     />North
                                 </label>
                               </div>
                               <div class="form-check">
                                 <label class="form-check-label" for="radio2">
                                   <input
                                     checked={this.state.selectedOption === "option2"}
                                     onChange={this.onValueChange}
                                     type="radio"
                                     class="form-check-input"
                                     id="radio2" name="optradio"
                                     value="option2"/>Center
                                 </label>
                               </div>
                               <div class="form-check">
                                 <label class="form-check-label">
                                   <input
                                     checked={this.state.selectedOption === "option3"}
                                     onChange={this.onValueChange}
                                     type="radio"
                                     class="form-check-input"
                                     id="radio3"
                                     name="optradio"
                                     value="option3"/>South
                                 </label>
                               </div>
                               <button className="btn btn-default" type="submit">
                                  Select
                              </button>
                              </form>
                              <select class="form-control mt-3" id="sel1"  onChange={this.onValueChange}>
                                <option value="0" disabled selected>Select option:</option>
                                <option value="1">up to 1kg</option>
                                <option value="2">up to 10kg</option>
                                <option value="3">up to 20kg</option>
                              </select>
                            </div>
                        </div>

                          <div class="modal-footer">
                            <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>:
                null
              )
            })}
              </div>
              <div className="col-lg-4 col-12">
                <div className="card mb-5">
                  <div className="card-body">
                    <h4 className="card-title text-muted text-uppercase text-center">Business</h4>
                    <h5 className="text-muted text-center">&#10038; Starts at</h5>
                    <h6 className="card-price text-center">18 ILS<span className="period">/per package</span></h6>


                    <ul className="fa-ul">
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>Duration: 1-4 days</li>
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>Time: Activity time</li>
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>Email notifications</li>
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>SMS notifications</li>
                    </ul>
                  </div>
                  <div className="card-footer">
                    <a href="#" className="btn btn-block text-uppercase">Continue</a>
                  </div>
                </div>
              </div>
              <div className="col-lg-4 col-12">
                <div className="card mb-5">
                  <div className="card-body">
                    <h4 className="card-title text-muted text-uppercase text-center">
                      <i class="far fa-star"></i>
                      Bespoke
                    </h4>
                    <h5 className="text-muted text-center">&#10038; Starts at</h5>
                    <h6 className="card-price text-center">40 ILS<span className="period">/Per package</span></h6>

                    <ul className="fa-ul">
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>Duration: 1-2 days</li>
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>Time: Unlimited</li>
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>Email notifications</li>
                      <li><span className="fa-li"><i className="fas fa-check"></i></span>SMS notifications</li>
                    </ul>
                  </div>
                  <div className="card-footer">
                    <a href="#" className="btn btn-block text-uppercase">Continue</a>
                  </div>
                </div>
              </div>
        </section>

      </div>
    )
  }
}

export default TransactionContent
