import React from 'react';
import './card.css';
import {CardItem} from'./card_json.js';
var myCards = "";
var text = "";
var timer = "";
var item;

for (item of myJSON) {
  for(var x of item.recipe){
    text+=x.quantity + " "+ x.product + "."
  };
  for(var y of item.time){
    timer+=y.Prep + " "+y.Cook+" "+y.Total+"."
  };
    myCards+= "<div class='card'>"+
              "<img class='card-img-top' src='"+item.pic_name+"' style='width:20%'>"+
              "<div class='card-body'>"+
              "<h4 class='card-title'>'"+item.name+"'</h4>"+
              "<span class='card-text'>'"+item.description+"'</span>"+
              "<p class='card-text'>'"+text+"'</p>"+
              "<p class='card-text'>'"+item.Preparation+"'</p>"+
              "<p class='card-text'>'"+timer+"'</p>"+
              "</div>"+
              "</div>"
              ;
              text= "";
              timer="";

};
document.getElementById("content").innerHTML= myCards;

