import React, { Component } from 'react';
import './button.css';


class Button extends React.Component{
  state ={clicked:false}

  handleClick = ()=>{
    this.setState({ clicked: !this.state.clicked})
  }
  render(){
    return(
      <div>
        <button type="button" onClick={handleClick}>
          Click me!
        </button>
      </div>

      )
}
}

export default Button;
