import React from 'react';
import './search.css'
import { makeStyles, createStyles, Theme } from '@material-ui/core/styles';


const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    closebtn: {
      display:'none'
    },
  }),
);

export default function SearchBox(){

    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const closeSearch = () => {
     setOpen(false);
   };
   const openSearch = () => {
     setOpen(!open);
   };
    return(
      <div>
        <div id="myOverlay" class="overlay">
          <span className={classes.closebtn}  onclick={closeSearch} title="Close Overlay">hiii</span>
          <div className="overlay-content">
            <form action="/action_page.php">
              <input type="text" placeholder="Search.." name="search"/>
              <button type="submit"><i class="fa fa-search"></i></button>
            </form>
          </div>
        </div>

        <h2>Fullscreen Search</h2>
        <p>Click on the button to open the search box.</p>
        <button open={open}  onclick={openSearch}>Open Search Box</button>
      </div>
    )
  }
